'use strict'

var simpleClustering = require("..");

var a = [
    [1,2,0,0],
    [1,2,0,0],
    [0,0,3,4],
    [0,0,5,6]
];
//Out: values, indexes, assignment matrix
describe('Simple clustering output', function () {
    it('values', function () {
        var values = simpleClustering(a,{threshold:0,out:"values"});
        values.should.be.eql([[[1,2], [1,2]], [[3,4], [5,6]]]);
    });
    it('assignment', function () {
        var assignment = simpleClustering(a,{threshold:0,out:"assignment"});
        assignment.should.be.eql([[1,1,0,0],[0,0,1,1]]);
    });
    it('indexes', function () {
        var indexes = simpleClustering(a,{threshold:0,out:"indexes"});
        indexes.should.be.eql([[0,1],[2,3]]);
    });
});






